function addBinary(a, b) {
    let result = [];
    let carry = 0;
    let i = a.length - 1, j = b.length - 1;

    while (i >= 0 || j >= 0 || carry) {
        let digitSum = carry;

        if (i >= 0) {
            digitSum += parseInt(a[i]);
            i--;
        }

        if (j >= 0) {
            digitSum += parseInt(b[j]);
            j--;
        }

        result.push((digitSum % 2).toString());
        carry = Math.floor(digitSum / 2);
    }

    return result.reverse().join('');
}

