class Solution {
    public int longestIncreasingSubsequence(int[] nums) {
        if (nums.length == 0) return 0;

        int n = nums.length;
        // dp[i] = length of LIS ending at index i
        int[] dp = new int[n];
        Arrays.fill(dp, 1);

        for (int i = 1; i < n; i++) {
            for (int j = 0; j < i; j++) {
                if (nums[j] < nums[i]) {
                    dp[i] = Math.max(dp[i], dp[j] + 1);
                }
            }
        }

        int maxLength = dp[0];
        for (int length : dp) {
            maxLength = Math.max(maxLength, length);
        }

        return maxLength;
    }
}
