python.solution_code:
def asteroidCollision(asteroids):
    stack = []

    for asteroid in asteroids:
        # Process current asteroid
        while stack and asteroid < 0 < stack[-1]:
            # Collision occurs: right-moving vs left-moving
            if stack[-1] < -asteroid:
                # Right-moving asteroid explodes
                stack.pop()
                continue
            elif stack[-1] == -asteroid:
                # Both asteroids explode
                stack.pop()
            # Left-moving asteroid explodes (asteroid >= 0 or no collision)
            break
        else:
            # No collision or asteroid survives
            stack.append(asteroid)

    return stack

javascript.solution_code:
function asteroidCollision(asteroids) {
    const stack = [];

    for (const asteroid of asteroids) {
        // Process current asteroid
        while (stack.length && asteroid < 0 && stack[stack.length - 1] > 0) {
            // Collision occurs: right-moving vs left-moving
            if (stack[stack.length - 1] < -asteroid) {
                // Right-moving asteroid explodes
                stack.pop();
                continue;
            } else if (stack[stack.length - 1] === -asteroid) {
                // Both asteroids explode
                stack.pop();
            }
            // Left-moving asteroid explodes (asteroid >= 0 or no collision)
            break;
        }

        // If we didn't break, add the asteroid
        if (!stack.length || asteroid > 0 || stack[stack.length - 1] < 0) {
            stack.push(asteroid);
        }
    }

    return stack;
}

