java.solution_code:
class Solution {
    public int[] asteroidCollision(int[] asteroids) {
        Stack<Integer> stack = new Stack<>();

        for (int asteroid : asteroids) {
            // Process current asteroid
            while (!stack.isEmpty() && asteroid < 0 && stack.peek() > 0) {
                // Collision occurs: right-moving vs left-moving
                if (stack.peek() < -asteroid) {
                    // Right-moving asteroid explodes
                    stack.pop();
                    continue;
                } else if (stack.peek() == -asteroid) {
                    // Both asteroids explode
                    stack.pop();
                }
                // Left-moving asteroid explodes (asteroid >= 0 or no collision)
                break;
            }

            // If we didn't break, add the asteroid
            if (stack.isEmpty() || asteroid > 0 || stack.peek() < 0) {
                stack.push(asteroid);
            }
        }

        // Convert stack to array
        int[] result = new int[stack.size()];
        for (int i = stack.size() - 1; i >= 0; i--) {
            result[i] = stack.pop();
        }

        return result;
    }
}
