class Solution {
    private Map<Integer, Integer> inorderMap;

    public TreeNode constructBinaryTreeFromPreorderAndInorderTraversal(int[] preorder, int[] inorder) {
        if (preorder.length == 0 || inorder.length == 0) {
            return null;
        }

        // Create hashmap for O(1) lookups in inorder array
        inorderMap = new HashMap<>();
        for (int i = 0; i < inorder.length; i++) {
            inorderMap.put(inorder[i], i);
        }

        return build(preorder, 0, preorder.length - 1, 0, inorder.length - 1);
    }

    private TreeNode build(int[] preorder, int preStart, int preEnd, int inStart, int inEnd) {
        if (preStart > preEnd) {
            return null;
        }

        // Root is always first element in preorder range
        int rootVal = preorder[preStart];
        TreeNode root = new TreeNode(rootVal);

        // Find root position in inorder array
        int rootIdx = inorderMap.get(rootVal);
        int leftSize = rootIdx - inStart;

        // Build left and right subtrees
        root.left = build(preorder, preStart + 1, preStart + leftSize, inStart, rootIdx - 1);
        root.right = build(preorder, preStart + leftSize + 1, preEnd, rootIdx + 1, inEnd);

        return root;
    }
}
