def capacityToShipPackagesWithinDDays(weights, days):
    def canShip(capacity):
        days_needed = 1
        current_weight = 0

        for weight in weights:
            if current_weight + weight > capacity:
                days_needed += 1
                current_weight = weight
            else:
                current_weight += weight

        return days_needed <= days

    left = max(weights)  # Minimum capacity (largest package)
    right = sum(weights)  # Maximum capacity (all packages in one day)

    while left < right:
        mid = (left + right) // 2
        if canShip(mid):
            right = mid  # Try smaller capacity
        else:
            left = mid + 1  # Need larger capacity

    return left

javascript.solution_code:
function capacityToShipPackagesWithinDDays(weights, days) {
    function canShip(capacity) {
        let daysNeeded = 1;
        let currentWeight = 0;

        for (const weight of weights) {
            if (currentWeight + weight > capacity) {
                daysNeeded++;
                currentWeight = weight;
            } else {
                currentWeight += weight;
            }
        }

        return daysNeeded <= days;
    }

    let left = Math.max(...weights);  // Minimum capacity (largest package)
    let right = weights.reduce((sum, weight) => sum + weight, 0);  // Maximum capacity

    while (left < right) {
        const mid = Math.floor((left + right) / 2);
        if (canShip(mid)) {
            right = mid;  // Try smaller capacity
        } else {
            left = mid + 1;  // Need larger capacity
        }
    }

    return left;
}

