java.solution_code:
class Solution {
    public int capacityToShipPackagesWithinDDays(int[] weights, int days) {
        // Helper method to check if we can ship with given capacity
        boolean canShip(int capacity) {
            int daysNeeded = 1;
            int currentWeight = 0;

            for (int weight : weights) {
                if (currentWeight + weight > capacity) {
                    daysNeeded++;
                    currentWeight = weight;
                } else {
                    currentWeight += weight;
                }
            }

            return daysNeeded <= days;
        }

        int left = Arrays.stream(weights).max().getAsInt();  // Minimum capacity
        int right = Arrays.stream(weights).sum();  // Maximum capacity

        while (left < right) {
            int mid = left + (right - left) / 2;
            if (canShip(mid)) {
                right = mid;  // Try smaller capacity
            } else {
                left = mid + 1;  // Need larger capacity
            }
        }

        return left;
    }
}
