def houseRobber(n):
    if n <= 0:
        return 0
    if n == 1:
        return 1

    # Memoization table
    dp = [-1] * (n + 1)
    dp[0] = 0
    dp[1] = 1

    def solve(i):
        if dp[i] != -1:
            return dp[i]

        # Recurrence relation (adjust based on problem)
        dp[i] = solve(i - 1) + solve(i - 2)
        return dp[i]

    return solve(n)

