class Solution {
    public int houseRobber(int n) {
        if (n <= 0) return 0;
        if (n == 1) return 1;

        // Memoization table
        int[] dp = new int[n + 1];
        Arrays.fill(dp, -1);
        dp[0] = 0;
        dp[1] = 1;

        return solve(n, dp);
    }

    private int solve(int i, int[] dp) {
        if (dp[i] != -1) return dp[i];

        // Recurrence relation (adjust based on problem)
        dp[i] = solve(i - 1, dp) + solve(i - 2, dp);
        return dp[i];
    }
}
