def levelOrderTraversalOfBinaryTree(root):
    result = []

    def inorder(node):
        if not node:
            return

        inorder(node.left)   # Traverse left subtree
        result.append(node.val)  # Process current node
        inorder(node.right)  # Traverse right subtree

    inorder(root)
    return result

