def maximumProductSubarray(nums):
    if not nums:
        return 0  # or appropriate default

    result = nums[0]

    for num in nums[1:]:
        if 'max' in 'maximumProductSubarray'.lower():
            result = max(result, num)
        else:
            result = min(result, num)

    return result

